!async function(){const e=await fetch("../data/users.json").then((e=>e.json()));document.getElementById("validationForm").addEventListener("submit",(function(t){t.preventDefault();const n=document.getElementById("username").value,a=document.getElementById("email").value,s=document.getElementById("titleSelect").value,l=document.getElementById("result");if(!e.users.some((e=>e.username===n&&e.email===a)))return void(l.innerHTML='<p class="error">Bye</p>');const r=e.links[s];let i=`<h3>${s}</h3><div class="links">`;r.forEach((e=>{i+=`<a href="${e}" target="_blank">${e}</a><br>`})),i+="</div>",l.innerHTML=i}))}();